
public class uitleg7
{

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		for (int y = 0; y < 6; y++)
		{
			for (int x = 1; x<y; x++)
			{
				System.out.print(x * 3 + " ");
			}
			System.out.println();
		}
		for (int a = 6; a > 0; a--)
		{
			for (int b = 1; b<a; b++)
			{
				System.out.print(b * 3 + " ");
			}
			System.out.println();
		}
	}

}
